use regex::Regex;

pub fn matches_tx(text: &str) -> bool {
    let pattern = r"^(tx1_hash|tx2_hash|tx4|tx58|tx58_len|tx64|tx64_len|tx_1|tx_2|tx_3|tx_4|tx_5|tx_account_locks|tx_account_locks_result|tx_account_locks_results|tx_after_stale|tx_anf|tx_approve|tx_attempted_allocation_size|tx_badsig_serialized_encoded|tx_batches|tx_before_stale|tx_by_addr|tx_by_addr_cells|tx_by_addr_data|tx_by_addr_error|tx_by_addr_info|tx_by_addr_transaction_error|tx_by_addr_transaction_info|tx_by_addrs|tx_bytes_delta|tx_cake|tx_carrier|tx_cells|tx_chunk|tx_client|tx_clone|tx_colls|tx_colls_delta|tx_compressed|tx_cost1|tx_cost2|tx_cost3|tx_cost_option|tx_cost_results|tx_count_after|tx_count_before|tx_deletion_rows|tx_details|tx_dora|tx_dora_read|tx_drops|tx_drops_delta|tx_encoding|tx_end|tx_entries|tx_error|tx_error_compute_consumed|tx_errs|tx_errs_delta|tx_execution_cost|tx_fail|tx_fee|tx_fifo|tx_fifo_delta|tx_from_main|tx_generator_threads|tx_hashes|tx_health|tx_id|tx_idx|tx_info|tx_info_res|tx_infos|tx_invalid_account_index|tx_invalid_program_index|tx_invalid_recent_blockhash|tx_key|tx_ky|tx_landed_slot|tx_log_writer|tx_master|tx_no_ver|tx_offset_recycler|tx_packets|tx_packets_delta|tx_pass|tx_path|tx_receiver|tx_rent|tx_req|tx_resp|tx_result|tx_results|tx_script|tx_send_elapsed|tx_sender|tx_sender_done|tx_sender_shutdown|tx_sent_count|tx_ser|tx_serialized_encoded|tx_sockets|tx_start|tx_starting_index|tx_status|tx_status1|tx_status2|tx_str|tx_sub|tx_timestamp|tx_to_worker|tx_total_us|tx_transfer_1_to_2|tx_transfer_mint_to_1|tx_type|tx_vector|tx_wide_compute_cap|txcard|txid|txjsonrpcmessage|txn_acct_index|txn_acct_indices|txn_mask|txn_signature|txnop|txnopresponse|txns|txoffsets|txresult|txs1|txs2|txs_batch|txs_costs|txs_errors|txs_landed|txs_per_entry|txs_per_sec|txs_processed|txs_receiver|txs_refs|txs_sender|txs_to_resend_over_tpu|txside|txsigner|txt2pair|txt_attn|txt_field|txt_ids|txt_in|txt_k|txt_mlp|txt_mod|txt_mod1|txt_mod2|txt_modulated|txt_norm1|txt_norm2|txt_q|txt_v|txtx)$";
    let re = Regex::new(pattern).unwrap();
    re.is_match(text)
}
