    let pattern = r"^(ip1|ip2|ip3|ip4_uri_host|ip6|ip6_uri_host|ip6u|ip_0|ip_1|ip_addr_1|ip_addr_2|ip_addr_byte|ip_addr_column|ip_addr_field|ip_addr_field_hash_map|ip_addr_options|ip_addr_v6|ip_addr_values|ip_addresses|ip_addrs|ip_cache|ip_code|ip_col|ip_constant_info|ip_echo_client|ip_echo_server_addr|ip_echo_server_request|ip_echo_server_request_length|ip_echo_server_request_with_binding|ip_echo_server_response_length|ip_echo_server_threads|ip_external|ip_families|ip_from|ip_from_id|ip_index|ip_literal|ip_local|ip_mask_to_prefix|ip_net|ip_prefix|ip_range|ip_range_tests|ip_reader|ip_str|ip_string|ip_strings|ip_to_u128|ip_type|ip_u128|ip_v4|ipad|ipaddr_const|ipaddrmapping|ipaddrssaturated|ipam_address|iparent|ipath_abempty|ipath_absolute|ipath_empty|ipath_noscheme|ipath_rootless|ipc_channel|ipc_url|ipchandshake|ipcodecparams|ipconeshotserver|ipcountry|ipcreceiver|ipechoservermessage|ipechoserverresponse|ipechoserverthreadsarg|ipelines|ipfamilies|ipformaterror|ipmatcher|ipns|ipod|ipp|ipp_a|ipp_b|ipp_proof|ipr|ipro|iprod|ipromiseitssafethistime|ipropertystore|ips_field|ips_file|ipsfile|ipsu|ipsumË‡|ipsypetbmvfkhesutoxycrucd6z65g3mtmxdjrbvecadsmz1jqjsd6f7mlh4mt6lkxvt8ftnmt6einmqigoqlldup4vwrvnosqeqfevrebgwb7ecoijjasththioj|iptables|iptype|ipv4_address|ipv4_local|ipv4_new|ipv4_path|ipv4_public|ipv4address|ipv4broadcast|ipv4local|ipv4network|ipv4only|ipv6_address|ipv6_disabled_lo|ipv6_enabled|ipv6_enabled_on_localhost|ipv6_from_long_dec_to_short_hex|ipv6_localhost_enabled|ipv6_path|ipv6_slaac|ipv6address|ipv6network|ipv6only|ipvfuture|ipykernel|ipykernel_check|ipykernel_launcher)$";
    let re = Regex::new(pattern).unwrap();
