int utf8len(const char* s) {
  int l=0;
  for (int i=0; s[i] != '\0'; i++)
    if ((s[i] & 0xc0) != 0x80)
      l++;
  return l;
}

int yy_input_proc(char* buf, int size, yyscan_t yyscanner);
